mapper.select:
		select p.*,t.uid,t.name from T_PROGRAMS p left join T_TRAIN t on p.trainId=t.uid where 1=1
		
			and p.name like concat('%',#{name[0]},'%')
		
		
			and t.uid=#{trainId[0]}			
		
		
      		ORDER BY #{orderBy}
    	
    	LIMIT #{limit},#{offset}

    SELECT COUNT(*) FROM T_PROGRAMS p left join T_TRAIN t on p.trainId=t.uid where 1=1
    
    	and p.name like concat('%',#{name[0]},'%')
    
    
		and t.uid=#{trainId[0]}
